def fizz_buzz(n: int):
    """Return the number of times the digit 7 appears in integers less than n which are divisible by 11 or 13.
    >>> fizz_buzz(50)
    0
    >>> fizz_buzz(78)
    2
    >>> fizz_buzz(79)
    3
    """
def fizz_buzz(n: int):
    """Return the number of times the digit 7 appears in integers less than n which are divisible by 11 or 13."""
    def has_digit_7(num: int):
        return '7' in str(num)

    count = 0
    for i in range(1, n):
        if (i % 11 == 0 or i % 13 == 0) and has_digit_7(i):
            count += 1
    return count

