    solve("#a@C") = "#A@c"
    """
def solve(s):
    changed = ""
    has_letters = any(char.isalpha() for char in s)

    # Reverse case of letters or reverse the entire string
    for char in s:
        if char.isalpha():
            if char.islower():
                changed += char.upper()
            else:
                changed += char.lower()
        else:
            changed += char

    if not has_letters:
        return changed[::-1]

    return changed

