    For lst = [0,81,12,3,1,21] the output should be 3
    For lst = [0,8,1,2,1,7] the output should be 7
    """
def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(n**0.5)+1):
        if n % i == 0:
            return False
    return True

def sum_of_digits(n):
    return sum(int(d) for d in str(n))

def skjkasdkd(lst):
    primes = [num for num in lst if is_prime(num)]
    if not primes:
