from typing import List


def string_xor(a: str, b: str) -> str:
    """ Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor('010', '110')
    '100'
    """
def string_xor(a: str, b: str) -> str:
    """Perform binary XOR on input strings and return result as a string."""
    result = ""
    for i in range(min(len(a), len(b))):
        if a[i] == b[i]:
            result += '0'
        else:
            result += '1'

    # Append remaining characters of longer string if any
    if len(a) > len(b):
        result += a[len(b):]
    elif len(a) < len(b):
        result += b[len(a):]

    return result

