def specialFilter(nums):
    """Write a function that takes an array of numbers as input and returns 
    the number of elements in the array that are greater than 10 and both 
    first and last digits of a number are odd (1, 3, 5, 7, 9).
    For example:
    specialFilter([15, -73, 14, -15]) => 1 
    specialFilter([33, -2, -3, 45, 21, 109]) => 2
    """
def specialFilter(nums):
    """Return the number of elements in the array that are greater than 10 
    and have odd first and last digits."""
    def is_odd(num):
        """Check if a number is odd."""
        return num % 2 != 0

    def first_digit_odd(n):
        """Check if the first digit of a number is odd."""
        return is_odd(int(str(n)[0]))

    def last_digit_odd(n):
        """Check if the last digit of a number is odd."""
        return is_odd(int(str(n)[-1]))

    # Filter the numbers based on the criteria
    filtered_nums = [num for num in nums if num > 10 and first_digit_odd(num) and last_digit_odd(num)]

    return len(filtered_nums)

# Testing the function with examples
assert specialFilter([15, -73, 14, -15]) == 1
assert specialFilter([33, -2, -3, 45, 21, 109]) == 2

