def is_prime(n):
    """Helper function to check if a number is prime."""
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def is_multiply_prime(a):
    """Return True if the given number is the multiplication of 3 prime numbers, False otherwise."""
    if a <= 1:
        return False
    for i in range(2, a):
        if is_prime(i):
            for j in range(i, a):
                if is_prime(j):
                    for k in range(j, a):
                        if is_prime(k) and i * j * k == a:
                            return True
    return False

