def histogram(test):
    """Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}

    """
def histogram(test):
    """
    Given a string representing a space separated lowercase letters, return a dictionary
    of the letter with the most repetition and containing the corresponding count.
    If several letters have the same occurrence, return all of them.
    """
    if not test:
        return {}  # Return empty dictionary if input is empty string

    counts = {}  # Dictionary to store counts of each letter
    max_count = 0  # Variable to store maximum count

    # Count occurrences of each letter
    for letter in test.split():
        counts[letter] = counts.get(letter, 0) + 1
        max_count = max(max_count, counts[letter])

    # Find letters with maximum count
    result = {letter: count for letter, count in counts.items() if count == max_count}

    return result

